mapper.select:
SELECT apps.id id, apps.wanted_id wantedId, apps.resume_id resumeId, apps.state state, apps.created created, wan.position_code_id positionCodeId, wan.company_id companyId, com.company_name companyName, poco.name positionCodeName, res.user_id userId FROM application_status apps INNER JOIN wanteds wan ON apps.wanted_id = wan.id INNER JOIN companys com ON wan.company_id = com.id INNER JOIN positions_code poco ON wan.position_code_id = poco.id INNER JOIN resumes res ON apps.resume_id = res.id WHERE res.user_id = #{id}
AND com.company_name LIKE CONCAT('%',#{keyword},'%')
ORDER BY apps.id DESC
