mapper.select:
SELECT totalCount, totalPage, currentPage, if(currentPage = 0, 1, 0) FIRST, if(currentPage = totalPage - 1, 1, 0) LAST FROM ( SELECT COUNT(*) totalCount, CEIL(COUNT(*)/16) totalPage, 0 currentPage, 0 FIRST, 0 LAST FROM wanteds w LEFT OUTER JOIN companys c ON w.company_id = c.id LEFT OUTER JOIN positions_code pc ON w.position_code_id = pc.id LEFT OUTER JOIN careers_code cc ON w.career_code_id = cc.id WHERE w.enddate > NOW()
AND pc.name = #{keywordDto.positionsCodeNames}
AND c.region_code_name = #{keywordDto.regionsCodeNames}
AND cc.name = #{keywordDto.CarrerCodeNames}
) result

SELECT l.like_count + w.view_count total_count ,w.id, w.position_code_id, w.career_code_id, w.company_id, w.title, w.detail, w.view_count, w.created, w.enddate, c.company_name, c.region_code_name, c.photo, pc.name, cc.name FROM wanteds w LEFT OUTER JOIN companys c ON w.company_id = c.id LEFT OUTER JOIN (SELECT *, COUNT(user_id) like_count FROM likes GROUP BY wanted_id) l ON l.wanted_id = w.id LEFT OUTER JOIN positions_code pc ON w.position_code_id = pc.id LEFT OUTER JOIN careers_code cc ON w.career_code_id = cc.id WHERE w.enddate > NOW()
AND pc.name = #{keywordDto.positionsCodeNames}
AND c.region_code_name = #{keywordDto.regionsCodeNames}
AND cc.name = #{keywordDto.CarrerCodeNames}
AND ( SELECT distinct(skills_code_id) FROM myskills mk LEFT OUTER JOIN skills_code sc ON mk.skills_code_id = sc.id WHERE mk.wanted_id = w.id AND sc.name = #{keywordDto.skillsCodeNames.get(index)}
ORDER BY w.created DESC OFFSET #{keywordDto.startNum} ROWS FETCH NEXT 16 ROWS ONLY
